import React from 'react';

const Stats = () => {
  const geographicalFootprint = [
    { number: "4", label: "South West", details: "Lagos, Ogun, Oyo, Ondo" },
    { number: "1", label: "North Central", details: "Abuja (FCT)" },
    { number: "1", label: "South East", details: "Imo" },
    { number: "2", label: "North West", details: "Kano, Jigawa" },
    { number: "1", label: "North East", details: "Adamawa" }
  ];

  return (
    <div className="container-fluid gtco-numbers-block">
      <div className="container" style={{position: 'relative', paddingTop: '60px', paddingBottom: '60px'}}>
        <div style={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          width: '80%',
          maxWidth: '600px',
          opacity: 0.12,
          zIndex: 0
        }}>
          <img 
            src="images/nigerian-map.svg" 
            alt="Nigeria Map" 
            style={{
              width: '100%',
              height: 'auto',
              filter: 'sepia(100%) saturate(300%) hue-rotate(70deg)'
            }}
          />
        </div>
        <div className="row" style={{position: 'relative', zIndex: 1}}>
          {geographicalFootprint.map((region, index) => (
            <div key={index} className="col-lg-2 col-md-4 col-sm-6 mb-3">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">{region.number}</h5>
                  <p className="card-text">{region.label}</p>
                  <small className="text-muted" style={{fontSize: '0.8rem'}}>{region.details}</small>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Stats;
