import React from 'react';

const Stats = () => {
  const geographicalFootprint = [
    { number: "4", label: "South West", details: "Lagos, Ogun, Oyo, Ondo" },
    { number: "1", label: "North Central", details: "Abuja (FCT)" },
    { number: "1", label: "South East", details: "Imo" },
    { number: "2", label: "North West", details: "Kano, Jigawa" },
    { number: "1", label: "North East", details: "Adamawa" }
  ];

  return (
    <div className="container-fluid gtco-numbers-block">
      <div className="container" style={{position: 'relative', paddingTop: '0px', paddingBottom: '80px', minHeight: '600px'}}>
        {/* Section Title */}
        <div style={{
          textAlign: 'center',
          marginBottom: '15px',
          position: 'relative',
          zIndex: 2
        }}>
          <h2 style={{
            fontSize: '2.5rem',
            fontWeight: 'bold',
            color: '#2d5016',
            marginBottom: '10px'
          }}>Our Geographical Footprint</h2>
          <p style={{
            fontSize: '1.1rem',
            color: '#4a7c59'
          }}>Serving Nigeria Across Five Regions</p>
        </div>

        {/* Nigeria Map Background */}
        <div style={{
          position: 'absolute',
          top: '80px',
          left: '50%',
          transform: 'translateX(-50%)',
          width: '90%',
          maxWidth: '700px',
          opacity: 0.25,
          zIndex: 0
        }}>
          <img 
            src="images/nigerian-map.svg" 
            alt="Nigeria Map" 
            style={{
              width: '100%',
              height: 'auto',
              filter: 'sepia(100%) saturate(300%) hue-rotate(70deg)'
            }}
          />
        </div>

        {/* Left Side Images */}
        <div style={{
          position: 'absolute',
          left: '5%',
          top: '120px',
          width: '150px',
          zIndex: 1
        }}>
          <img 
            src="images/geographical_footprint/1.jpg" 
            alt="Location 1" 
            style={{
              width: '100%',
              height: '120px',
              objectFit: 'cover',
              borderRadius: '8px',
              border: '3px solid #4a7c59',
              marginBottom: '15px',
              boxShadow: '0 4px 10px rgba(45, 80, 22, 0.2)'
            }}
          />
          <img 
            src="images/geographical_footprint/2.jpg" 
            alt="Location 2" 
            style={{
              width: '100%',
              height: '120px',
              objectFit: 'cover',
              borderRadius: '8px',
              border: '3px solid #4a7c59',
              boxShadow: '0 4px 10px rgba(45, 80, 22, 0.2)'
            }}
          />
        </div>

        {/* Right Side Images */}
        <div style={{
          position: 'absolute',
          right: '5%',
          top: '120px',
          width: '150px',
          zIndex: 1
        }}>
          <img 
            src="images/geographical_footprint/3.jpg" 
            alt="Location 3" 
            style={{
              width: '100%',
              height: '120px',
              objectFit: 'cover',
              borderRadius: '8px',
              border: '3px solid #4a7c59',
              marginBottom: '15px',
              boxShadow: '0 4px 10px rgba(45, 80, 22, 0.2)'
            }}
          />
          <img 
            src="images/geographical_footprint/4.jpg" 
            alt="Location 4" 
            style={{
              width: '100%',
              height: '120px',
              objectFit: 'cover',
              borderRadius: '8px',
              border: '3px solid #4a7c59',
              marginBottom: '15px',
              boxShadow: '0 4px 10px rgba(45, 80, 22, 0.2)'
            }}
          />
          <img 
            src="images/geographical_footprint/5.jpg" 
            alt="Location 5" 
            style={{
              width: '100%',
              height: '120px',
              objectFit: 'cover',
              borderRadius: '8px',
              border: '3px solid #4a7c59',
              boxShadow: '0 4px 10px rgba(45, 80, 22, 0.2)'
            }}
          />
        </div>
        
        {/* Regional Numbers and Labels positioned on the map */}
        <div style={{position: 'relative', zIndex: 1, height: '400px'}}>
          {/* South West - Bottom Left */}
          <div style={{
            position: 'absolute',
            left: '25%',
            top: '74%',
            textAlign: 'center'
          }}>
            <div style={{
              fontSize: '3rem',
              fontWeight: 'bold',
              color: '#2d5016',
              textShadow: '2px 2px 4px rgba(255,255,255,0.8)'
            }}>4</div>
            <div style={{
              fontSize: '1rem',
              fontWeight: '600',
              color: '#4a7c59',
              marginTop: '-10px'
            }}>South West</div>
            <div style={{
              fontSize: '0.75rem',
              color: '#6b9b37',
              marginTop: '2px'
            }}>Lagos, Ogun, Oyo, Ondo</div>
          </div>

          {/* North Central - Center, shifted right */}
          <div style={{
            position: 'absolute',
            left: '46%',
            top: '40%',
            transform: 'translateX(-50%)',
            textAlign: 'center'
          }}>
            <div style={{
              fontSize: '3rem',
              fontWeight: 'bold',
              color: '#2d5016',
              textShadow: '2px 2px 4px rgba(255,255,255,0.8)'
            }}>1</div>
            <div style={{
              fontSize: '1rem',
              fontWeight: '600',
              color: '#4a7c59',
              marginTop: '-10px'
            }}>North Central</div>
            <div style={{
              fontSize: '0.75rem',
              color: '#6b9b37',
              marginTop: '2px'
            }}>Abuja (FCT)</div>
          </div>

          {/* South East - Below North Central */}
          <div style={{
            position: 'absolute',
            left: '42%',
            top: '99%',
            textAlign: 'center'
          }}>
            <div style={{
              fontSize: '3rem',
              fontWeight: 'bold',
              color: '#2d5016',
              textShadow: '2px 2px 4px rgba(255,255,255,0.8)'
            }}>1</div>
            <div style={{
              fontSize: '1rem',
              fontWeight: '600',
              color: '#4a7c59',
              marginTop: '-10px'
            }}>South East</div>
            <div style={{
              fontSize: '0.75rem',
              color: '#6b9b37',
              marginTop: '2px'
            }}>Imo</div>
          </div>

          {/* North West - Top, shifted upward */}
          <div style={{
            position: 'absolute',
            left: '30%',
            top: '2%',
            textAlign: 'center'
          }}>
            <div style={{
              fontSize: '3rem',
              fontWeight: 'bold',
              color: '#2d5016',
              textShadow: '2px 2px 4px rgba(255,255,255,0.8)'
            }}>2</div>
            <div style={{
              fontSize: '1rem',
              fontWeight: '600',
              color: '#4a7c59',
              marginTop: '-10px'
            }}>North West</div>
            <div style={{
              fontSize: '0.75rem',
              color: '#6b9b37',
              marginTop: '2px'
            }}>Kano, Jigawa</div>
          </div>

          {/* North East - Top Right Inside Map */}
          <div style={{
            position: 'absolute',
            right: '28%',
            top: '15%',
            textAlign: 'center'
          }}>
            <div style={{
              fontSize: '3rem',
              fontWeight: 'bold',
              color: '#2d5016',
              textShadow: '2px 2px 4px rgba(255,255,255,0.8)'
            }}>1</div>
            <div style={{
              fontSize: '1rem',
              fontWeight: '600',
              color: '#4a7c59',
              marginTop: '-10px'
            }}>North East</div>
            <div style={{
              fontSize: '0.75rem',
              color: '#6b9b37',
              marginTop: '2px'
            }}>Adamawa</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Stats;
