import React, { useEffect } from 'react';

const Testimonials = () => {
  const testimonials = [
    {
      image: "images/customer1.jpg",
      name: "Lisa Gally",
      title: "Project Manager",
      quote: "Nam libero tempore, cum soluta nobis est eligendi optio cumque nihil impedit quo minus id quod maxime placeat"
    },
    {
      image: "images/customer2.jpg",
      name: "Missy Limana",
      title: "Project Manager",
      quote: "Nam libero tempore, cum soluta nobis est eligendi optio cumque nihil impedit quo minus id quod maxime placeat"
    },
    {
      image: "images/customer3.jpg",
      name: "Aana Brown",
      title: "Project Manager",
      quote: "Nam libero tempore, cum soluta nobis est eligendi optio cumque nihil impedit quo minus id quod maxime placeat"
    },
    {
      image: "images/customer3.jpg",
      name: "Aana Brown",
      title: "Project Manager",
      quote: "Nam libero tempore, cum soluta nobis est eligendi optio cumque nihil impedit quo minus id quod maxime placeat"
    }
  ];

  useEffect(() => {
    // Initialize owl carousel for testimonials
    if (window.$ && window.$('.owl-carousel1').length) {
      window.$('.owl-carousel1').owlCarousel({
        loop: true,
        center: true,
        margin: 0,
        responsiveClass: true,
        nav: false,
        responsive: {
          0: {
            items: 1,
            nav: false
          },
          600: {
            items: 1,
            nav: false
          },
          1000: {
            items: 3,
            nav: true,
            loop: false
          }
        }
      });
    }
  }, []);

  return (
    <div className="container-fluid gtco-testimonials">
      <div className="container">
        <h2>What our customers say about us.</h2>
        <div className="owl-carousel owl-carousel1 owl-theme">
          {testimonials.map((testimonial, index) => (
            <div key={index}>
              <div className="card text-center">
                <img className="card-img-top" src={testimonial.image} alt="" />
                <div className="card-body">
                  <h5>
                    {testimonial.name} <br/>
                    <span>{testimonial.title}</span>
                  </h5>
                  <p className="card-text">" {testimonial.quote} "</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Testimonials;
