import React, { useEffect } from 'react';

const News = () => {
  const news = [
    {
      image: "images/news1.jpg",
      title: "Aenean ultrices lorem quis blandit tempor urabitur accumsan.",
      excerpt: "Donec non sem mi. In hac habitasse platea dictumst. Nullam a feugiat augue, et porta metus. Nulla mollis lobortis leet. Maecenas tincidunt, arcu sed ornare purus risus . . ."
    },
    {
      image: "images/news2.jpg",
      title: "Nam vel nisi eget odio pulvinar iaculis. Fusce aliquet.",
      excerpt: "Donec non sem mi. In hac habitasse platea dictumst. Nullam a feugiat augue, et porta metus. Nulla mollis lobortis leet. Maecenas tincidunt, arcu sed ornare purus risus . . ."
    },
    {
      image: "images/news3.jpg",
      title: "Morbi faucibus odio sollicitudin risus scelerisque dignissim.",
      excerpt: "Donec non sem mi. In hac habitasse platea dictumst. Nullam a feugiat augue, et porta metus. Nulla mollis lobortis leet. Maecenas tincidunt, arcu sed ornare purus risus . . ."
    }
  ];

  // Duplicate news for carousel
  const allNews = [...news, ...news];

  useEffect(() => {
    // Initialize owl carousel for news
    if (window.$ && window.$('.owl-carousel2').length) {
      window.$('.owl-carousel2').owlCarousel({
        loop: true,
        center: false,
        margin: 0,
        responsiveClass: true,
        nav: true,
        responsive: {
          0: {
            items: 1,
            nav: false
          },
          600: {
            items: 2,
            nav: false
          },
          1000: {
            items: 3,
            nav: true,
            loop: true
          }
        }
      });
    }
  }, []);

  return (
    <div className="container-fluid gtco-news" id="news">
      <div className="container">
        <h2>Gallery</h2>
        <div className="owl-carousel owl-carousel2 owl-theme">
          {allNews.map((article, index) => (
            <div key={index}>
              <div className="card text-center">
                <img className="card-img-top" src={article.image} alt="" />
                <div className="card-body text-left pr-0 pl-0">
                  <h5>{article.title}</h5>
                  <p className="card-text">{article.excerpt}</p>
                  <a href="#">
                    READ MORE <i className="fa fa-angle-right" aria-hidden="true"></i>
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default News;
